            if(%killerDT.stat["plasmaKillDist"] < %dis){%killerDT.stat["plasmaKillDist"] = %dis;}
            if(%killerDT.stat["plasmaKillVV"] < %victimVel){%killerDT.stat["plasmaKillVV"] = %victimVel;}
