function getArmorBreakDown(%game,%dtStats){
   %avg[0] = getGameDataAvg(%game, %dtStats, "lArmorTimeTG");
   %avg[1] = getGameDataAvg(%game, %dtStats, "mArmorTimeTG");
   %avg[2] = getGameDataAvg(%game, %dtStats, "hArmorTimeTG");
   %armor[0] = "Light";
   %armor[1] = "Medium";
   %armor[2] = "Heavy";
   %total = %lavg + %mavg + %havg;
   %l = %max = 0;
      for(%x = 0; %x < 3; %x++){
         %total += %avg[%x];
         if(%avg[%x] > %max){
             %max = %avg[%x];
             %l = %x;
         }
      }
   return mFloor((%avg[0]/%total)*100) TAB  mFloor((%avg[1]/%total)*100) TAB  mFloor((%avg[2]/%total)*100) TAB  mFloor((%avg[%l]/%total)*100) TAB %armor[%l];
}
function getGameDataAvg(%game,%dtStats,%var){
   if(%dtStats.gameData[%game] && %dtStats.gameStats["totalGames","g",%game] != 0){
      %c = 0;
      %x = %dtStats.gameStats["statsOverWrite","g",%game];
      for(%i=0; %i < 32; %i++){
         %v = %x - %i;
         if(%v < 0)
            %v = $dtStats::MaxNumOfGames + %v;
         %pct = getField(%dtStats.gameStats["gamePCT","g",%game],%v);
         if(%pct > 90){
            %num = getField(%dtStats.gameStats[%var,"g",%game],%v);
            if(%num){
               %val += %num;
               %c++;
               if(%c > 16)
                  break;
            }
         }
      }
      if(%c > 0)
         return numReduce(mCeil(%val / %c),1);
   }
   return 0;
}

function getGameData(%game,%client,%var,%type,%value){
   if(%type $= "game"){
      %total = getField(%client.dtStats.gameStats[%var,"g",%game],%value);
      if(%total !$= "")
         return mFloatLength(%total,2) + 0;
      else
         error("Error getGameData" SPC %game SPC %client SPC %var SPC %type SPC %value);
   }
   else if(%type $= "total"){
      %total = getField(%client.dtStats.gameStats[%var,"t",%game],%value);
      if(strpos(%total,"%a") != -1){
        %total = getField(strreplace(%total,"%a","\t"),0);
      }
      if(%total !$= "")
         return numReduce(%total,1);
      else
         error("Error getGameData" SPC %game SPC %client SPC %var SPC %type SPC %value);
   }
   else if(%type $= "avg"){
      if(%client.dtStats.gameStats["totalGames","g",%game] != 0){
      %c = 0;
      %x = %client.dtStats.gameStats["statsOverWrite","g",%game];
      for(%i=0; %i < 16; %i++){
         %v = %x - %i;
         if(%v < 0)
            %v = $dtStats::MaxNumOfGames + %v;
         %num = getField(%client.dtStats.gameStats[%var,"g",%game],%v);
         if(%num $= ""){
            error("Error getGameData" SPC %game SPC %client SPC %var SPC %type SPC %value);
            break;
         }
         if(%num > 0 || %num < 0){
            %val += %num;
            %c++;
            if(%c >= %value)
               break;
         }
      }
      if(%c > 0)
         return numReduce(mCeil(%val / %c),1);
      }
   }
   return 0;
}

function numReduce(%num, %des) {
    if (%num $= "")
        return 0;

    if (strPos(%num, "x") != -1)
        return %num;

    %affixes = "KMGTPZY";
    %c = 0;

    while (%num > 1000 && %c < 7) {
        %num /= 1000;
        %c++;
    }

    %affix = %c > 0 ? getSubStr(%affixes, %c - 1, 1) : "";
    %formatted = mFloatLength(%num, %des) + 0;

    if (%c > 1 && %c < 10) {
        %seg = strReplace(getSubStr(%formatted, 0, strLen(%formatted) - 1), ".", "");
        %formatted = %seg $= "0" || %seg $= "00" ? getSubStr(%formatted, strLen(%formatted) - 1, 1) : %formatted;
    }

    return %formatted @ %affix;
}

