function getCIDRRange(%ip) {
   %ipField = strreplace(%ip, "/", "\t");
   %ip = getField(%ipField, 0);
   %ipMask = getField(%ipField, 1);
   for(%i = 0; %i < 32 - %ipMask; %i++){
      %host = %host @ "0";
      %broad = %broad @ "1";
   }
   // Convert IP address to integer
   %ipArray = strreplace(%ip, ".", "\t");

   %binIP = decToBin2(getField(%ipArray,0)) @ decToBin2(getField(%ipArray,1)) @ decToBin2(getField(%ipArray,2)) @ decToBin2(getField(%ipArray,3));
   %h =  getSubStr(%binIP, 0, %ipMask) @ %host;
   %b =  getSubStr(%binIP, 0, %ipMask) @ %broad;
   %start = binToDec2(getSubStr(%h,0,8)) TAB binToDec2(getSubStr(%h,8,8))  TAB binToDec2(getSubStr(%h,16,8)) TAB binToDec2(getSubStr(%h,24,8));
   %end =  binToDec2(getSubStr(%b,0,8)) TAB binToDec2(getSubStr(%b,8,8))  TAB binToDec2(getSubStr(%b,16,8)) TAB binToDec2(getSubStr(%b,24,8));
   //error(strreplace(%start, "\t", ".") SPC strreplace(%end, "\t", "."));
   //%testip = strreplace(%testip, ".", "\t");
    //for (%i = 0; %i < 4; %i++) {
             //%ipOctet = getField(%testip,%i);
             //%startOctet = getField(%start,%i);
             //%endOctet = getField(%end,%i);
//
            //if (%ipOctet < %startOctet || %ipOctet > %endOctet) {
                //return false;
            //} else if (%ipOctet > %startOctet && %ipOctet < %endOctet) {
                //return true;
            //}
   //}
   %ipcount=0;
   for (%i = getField(%start,0); %i <= getField(%end,0); %i++) {
      for (%j = getField(%start,1); %j <= getField(%end,1); %j++) {
         for (%k = getField(%start,2); %k <= getField(%end,2); %k++) {
            for (%l = getField(%start,3); %l <= getField(%end,3); %l++) {
               %currentIP = %i @ "_" @ %j @ "_" @ %k @ "_" @ %l;
               $dtIPList[%currentIP] = 1;
               $ipcount++;
               %ipcount++;
               if($ipcount > 2000000){
                  $ipcountLimit = 1;
               }
            }
         }
      }
   }
   return %ipcount;
}

function decToBin2(%dec){
   %bitlen = 8;
	for (%i = 0; %i < %bitlen; %i++){
		%test = mPow(2, (%bitlen-1)-%i);
		if (%dec >= %test){
			%bin = %bin @ "1";
			%dec -= %test;
		}
		else
			%bin =  %bin @ "0";
	}
	return %bin;
}

function binToDec2(%bin){
	%dec = 0;
	for (%i = 0; %i < strLen(%bin); %i++)
		%dec += getSubStr(%bin, %i, 1) * mPow(2, strLen(%bin) - %i - 1);
	return %dec;
}

