            %line = strreplace(%fobj.readLine(),"%t","\t");
            $pugMapDef[1] = getFields(%line, 2, getFieldCount(%line)-1);
            %line = strreplace(%fobj.readLine(),"%t","\t");
            $pugMapKills[1] = getFields(%line, 2, getFieldCount(%line)-1);


            %line = strreplace(%fobj.readLine(),"%t","\t");
            $pugMapName[2] = getFields(%line, 2, getFieldCount(%line)-1);
            %line = strreplace(%fobj.readLine(),"%t","\t");
