   if(!isEventPending($saveML))
      $saveML = ML.schedule(1000,"save", "serverStats/mapRot.cs" , 0);
   if(!isEventPending($saveMR))
      $saveMR = schedule(2000, 0, "export", "$dtFixedMap*", "serverStats/fixMapRot.cs", false );
}

